import React, { useEffect, useRef, useState } from "react";

const FloatingBubbles = () => {
  const [animationStopped, setAnimationStopped] = useState(false);
  const [selectedIndex, setSelectedIndex] = useState(null);
  const [screenSize, setScreenSize] = useState("desktop");

  const bubbles = [
    { id: 1, color: "bg-red-400" },
    { id: 2, color: "bg-green-400" },
    { id: 3, color: "bg-blue-400" },
    { id: 4, color: "bg-yellow-400" },
  ];

  // Detect screen size
  useEffect(() => {
    const updateScreenSize = () => {
      if (window.innerWidth < 640) setScreenSize("mobile"); // sm
      else if (window.innerWidth < 1024) setScreenSize("tablet"); // md
      else setScreenSize("desktop");
    };

    updateScreenSize();
    window.addEventListener("resize", updateScreenSize);
    return () => window.removeEventListener("resize", updateScreenSize);
  }, []);

  const handleBubbleClick = (index) => {
    setSelectedIndex(index);
    setAnimationStopped(true);
  };

  return (
    <div className="relative w-full h-screen overflow-hidden">
      {bubbles.map((bubble, index) => {
        const isSelected = selectedIndex === index;

        // Hide others when animation stopped
        if (animationStopped && !isSelected) return null;

        // Responsive fixed stop positions
        let stopStyle = {};
        if (isSelected && animationStopped) {
          if (screenSize === "mobile") {
            stopStyle = {
              position: "fixed",
              top: "5vh",
              left: "50%",
              transform: "translateX(-50%)",
              zIndex: 30,
            };
          } else if (screenSize === "tablet") {
            stopStyle = {
              position: "fixed",
              top: "3vh",
              left: "20vw",
              transform: "translate(-50%, -50%)",
              zIndex: 30,
            };
          } else {
            // Desktop
            stopStyle = {
              position: "fixed",
              top: "-1.5vh",
              left: "11.6vw",
              transform: "translate(-50%, -50%)",
              zIndex: 30,
            };
          }
        } else {
          stopStyle = {
            transform: `translateY(${index * 60}px)`, // your old floating style
          };
        }

        return (
          <div
            key={bubble.id}
            onClick={() => handleBubbleClick(index)}
            className={`absolute w-16 h-16 rounded-full ${bubble.color} cursor-pointer transition-all duration-500`}
            style={stopStyle}
          />
        );
      })}
    </div>
  );
};

export default FloatingBubbles;
