// components/Button.jsx
import React from "react";
import clsx from "clsx";

const sizes = {
  sm: "px-4 py-1 text-sm h-8",
  md: "px-6 py-2 text-base h-10",
  lg: "px-8 py-3 text-lg h-12",
  xl: "px-10 py-4 text-xl h-14",
};

const variants = {
  primary:
    "bg-[#A9CF45] hover:bg-[#8BB833] text-black border-1 border-[#96b34d] shadow-sm",
  outline:
    "bg-transparent text-[#414651] border-2 border-[#A9CF45] hover:bg-[#F5F8EC]",
};

const Button = ({ children, size = "md", variant = "primary", fullWidth = false, className }) => {
  return (
    <button
      className={clsx(
        "rounded-full font-semibold transition",
        sizes[size],
        variants[variant],
        fullWidth && "w-full",
        className
      )}
    >
      {children}
    </button>
  );
};

export default Button;
