import React, { useEffect, useState } from "react";

const categories = [
  "FARMERS",
  "HOUSEHOLDS",
  "FOOD & COOK",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
  "CONSTRUCTION",
  "WORKSHOPS",
  "KIDS",
];

const BubbleAnimation = ({
  onPositionChange,
  selectedCategory,
  setSelectedCategory,
  animationStopped,
  setAnimationStopped,
}) => {
  const [yPosition, setYPosition] = useState(0);
  const spacing = 320;
  const loopHeight = spacing * categories.length;

  // Animate loop
  useEffect(() => {
    if (animationStopped) return;
    let frameId;
    const animate = () => {
      setYPosition((prev) => (prev - 4 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };
    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, [loopHeight, animationStopped]);

  // Track bubble positions
  useEffect(() => {
    if (!onPositionChange) return;
    const container = document.querySelector("#bubble-container");
    if (!container) return;

    const bubbles = Array.from(
      container.querySelectorAll(".bubble-item")
    ).map((el) => {
      const rect = el.getBoundingClientRect();
      return {
        x: rect.left + rect.width / 2,
        y: rect.top + rect.height / 2,
        radius: rect.width / 2,
      };
    });
    onPositionChange(bubbles);
  }, [yPosition, onPositionChange]);

  const getY = (offset) => {
    const rawY = (yPosition + offset) % loopHeight;
    return rawY > loopHeight / 2 ? rawY - loopHeight : rawY;
  };

  return (
    <div className="absolute top-0 left-0 w-full h-full pointer-events-auto z-10 font-rubik">
      {/* Responsive container like STOQ logo wrapper */}
      <div
        id="bubble-container"
        className="relative w-[80vw] max-w-[800px] mx-auto"
        style={{
          top: "50%",
          transform: "translateY(-50%)",
        }}
      >
        {categories.map((text, i) => {
          const y = getY(i * spacing);
          const isSelected = selectedCategory === text;

          // Selected bubble fixed to O in logo
          const fixedStyle =
            isSelected && animationStopped
              ? {
                  position: "absolute",
                  top: "45%", // center on O
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                  zIndex: 30,
                  width: "26%", // responsive, relative to logo
                  aspectRatio: "1 / 1",
                }
              : {
                  transform: `translateY(${y}px)`,
                  width: "22%", // base bubble size relative to logo
                  aspectRatio: "1 / 1",
                };

          return (
            <div
              key={i}
              className="bubble-item absolute rounded-full font-bold text-[#1C230C] flex items-center justify-center text-[2vw] sm:text-[1.5vw] lg:text-[1vw] cursor-pointer"
              style={{
                ...fixedStyle,
                background: `linear-gradient(180deg, rgba(255,255,255,0.3) 0%, rgba(255,255,255,0.1) 100%)`,
                border: "1px solid rgba(255, 255, 255, 0.4)",
                boxShadow: `
                  inset 0 0 10px rgba(255, 255, 255, 0.3),
                  inset 0 2px 4px rgba(255, 255, 255, 0.2),
                  0 4px 8px rgba(0, 0, 0, 0.05)
                `,
                backdropFilter: "blur(20px)",
                WebkitBackdropFilter: "blur(20px)",
                opacity:
                  Math.abs(y) > loopHeight / 2 - 140 && !isSelected ? 0 : 1,
              }}
              onClick={() => {
                if (isSelected && animationStopped) {
                  setSelectedCategory(null);
                  setAnimationStopped(false);
                } else if (!animationStopped) {
                  setSelectedCategory(text);
                  setAnimationStopped(true);
                }
              }}
            >
              {text}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default BubbleAnimation;
