import React from "react";
import { motion } from "framer-motion";

const bubbleItems = ["KIDS", "FREELANCERS", "WELLNESS"];

const BubbleScrollSection = () => {
  return (
    <div className="relative h-screen w-full overflow-hidden">
      {/* Background Video */}
      <video
        autoPlay
        muted
        loop
        playsInline
        className="absolute top-0 left-0 w-full h-full object-cover z-0"
      >
        <source src="/videos/background.mp4" type="video/mp4" />
        Your browser does not support the video tag.
      </video>

      {/* Content Overlay */}
      <div className="relative z-10 flex flex-col justify-center items-center h-full text-center">
        {/* STOQ Line */}
        <h1 className="flex items-center text- font-extrabold text-[#8BC34A] tracking-wide">
          S T
          <div className="relative flex items-center justify-center w-[120px] h-[120px]">
            {/* Bubbles pass behind this O */}
            <div className="absolute top-[150%] left-1/2 -translate-x-1/2 -translate-y-1/2 w-[120px] h-[1000px] pointer-events-none z-10">
              {bubbleItems.map((text, i) => (
                <motion.div
                  key={i}
                  className="absolute left-0 w-[120px] h-[120px] rounded-full bg-white/80 text-[#1a1a1a] text-sm font-bold flex items-center justify-center shadow-md backdrop-blur-sm border border-white"
                  animate={{ y: ["100%", "-200%"], opacity: [1, 0.8, 0] }}
                  transition={{
                    duration: 6 + i,
                    repeat: Infinity,
                    repeatType: "loop",
                    delay: i * 0.6,
                  }}
                >
                  {text}
                </motion.div>
              ))}
            </div>

            {/* The 'O' */}
            <span className="relative z-20 text-8xl font-extrabold text-[#8BC34A]">O</span>
          </div>

          {/* The 'Q' */}
          <div className="relative w-[120px] h-[120px] text-8xl font-extrabold text-[#8BC34A] flex items-center justify-center">
            Q
            {/* TRY Bubble inside Q */}
            <div className="absolute w-[60px] h-[60px] rounded-full bg-black text-white text-lg font-bold flex items-center justify-center shadow-inner border-4 border-white">
              TRY
            </div>
          </div>
        </h1>

        {/* Subtitle */}
        <p className="mt-6 text-xl font-semibold text-[#4B6100]">SEE. DO. GROW</p>
      </div>
    </div>
  );
};

export default BubbleScrollSection;
