import React, { useEffect, useState } from "react";

const categories = [
  "FARMERS",
  "RESTAURANTS",
  "HOME & CARE",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
  "CONSTRUCTION",
  "WORKSHOPS",
  "KIDS",
  "SERVICES",
];

const BubbleAnimation = () => {
  const [yPosition, setYPosition] = useState(0);
  const spacing = 160;
  const loopHeight = 1600;

  useEffect(() => {
    let frameId;
    const animate = () => {
      setYPosition((prev) => (prev - 0.6 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };
    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, []);

  const getY = (offset) => (yPosition + offset) % loopHeight - loopHeight / 2;

  return (
    <div className="absolute inset-0 flex items-center justify-center z-10 pointer-events-none">
      <div className="relative w-[160px] h-full overflow-hidden">
        {categories.map((text, i) => (
          <div
            key={i}
            className="absolute left-1/2 top-1/2 w-[140px] h-[140px] -translate-x-1/2 -translate-y-1/2 rounded-full flex items-center justify-center shadow-xl text-black font-semibold text-[13px] tracking-wide"
            style={{
              transform: `translate(-50%, calc(-50% + ${getY(i * spacing)}px))`,
              background: "rgba(255, 255, 255, 0.3)",
              backdropFilter: "blur(12px)",
              WebkitBackdropFilter: "blur(12px)",
              border: "1px solid rgba(255, 255, 255, 0.6)",
            }}
          >
            {text}
          </div>
        ))}
      </div>
    </div>
  );
};

export default BubbleAnimation;
