import React, { useEffect, useRef } from "react";
import gsap from "gsap";

const categories = [
  "FARMERS",
  "RESTAURANTS",
  "HOME & CARE",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
];

const BubbleScrollSection = () => {
  const bubblesRef = useRef([]);

  useEffect(() => {
    gsap.to(bubblesRef.current, {
      yPercent: 100,
      repeat: -1,
      duration: 5,
      ease: "linear",
      stagger: 0.5,
    });
  }, []);

  const renderBubbles = () => {
    return Array.from({ length: 10 }).map((_, index) => (
      <div
        key={index}
        ref={(el) => (bubblesRef.current[index] = el)}
        className="absolute w-5 h-5 bg-white opacity-60 rounded-full left-1/2 transform -translate-x-1/2"
        style={{ top: `${-100 * index}px` }}
      />
    ));
  };

  return (
    <section className="relative w-full h-screen overflow-hidden flex items-center justify-center text-center text-green-700">
      {/* Background Video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover z-0"
        src="/assets/background.mp4"
        autoPlay
        loop
        muted
      ></video>

      {/* Bubbles in O */}
      <div className="absolute w-24 h-24 z-20 overflow-hidden top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
        {renderBubbles()}
      </div>

      {/* Main Content */}
      <div className="z-10 relative flex flex-col items-center">
        <div className="flex items-center gap-4 text-[100px] font-extrabold">
          <span>S</span>
          <span>T</span>
          <span className="relative">
            <div className="w-[120px] h-[120px] rounded-full border-8 border-green-500 flex items-center justify-center text-3xl font-semibold text-black bg-white">
              TRY
            </div>
          </span>
          <span>Q</span>
        </div>
        <div className="flex gap-8 mt-8">
          {["SERVICES", "CONSTRUCTION", "WORKSHOPS"].map((text, i) => (
            <div
              key={i}
              className="w-32 h-32 rounded-full bg-gradient-to-br from-green-300 to-white flex items-center justify-center font-bold shadow-md"
            >
              {text}
            </div>
          ))}
        </div>
        <p className="mt-8 text-xl text-green-700 font-bold">SEE. DO. GROW</p>
      </div>
    </section>
  );
};

export default BubbleScrollSection;
