// src/components/HeroSection.jsx
import React, { useEffect, useRef, useState } from "react";
import BubbleAnimation from "./BubbleAnimation";
import Navbar from "./Navbar";
import gsap from "gsap";

// 0 = green (default), 1 = black (gap)
const colorCycle = ["#6E8A28", "#303C14"];

const HeroSection = () => {
  const tryRef = useRef(null);
  const [colorIndex, setColorIndex] = useState(0); // Start with green
  const [bubbleDetected, setBubbleDetected] = useState(false);

  // ⏱ Timing-based color change logic
  useEffect(() => {
    let timer;
    if (bubbleDetected) {
      setColorIndex(0); // Green immediately
      clearTimeout(timer);
      timer = setTimeout(() => {
        setColorIndex(1); // Black after 2s if no bubble re-detected
      }, 2000);
    }
    return () => clearTimeout(timer);
  }, [bubbleDetected]);

  // GSAP animation for TRY bubble
  useEffect(() => {
    const el = tryRef.current;
    gsap.set(el, { scale: 1.5 });
    const tl = gsap.timeline({ repeat: -1, yoyo: true });
    tl.to(el, { scale: 2.9, duration: 4, ease: "power1.inOut" })
      .to(el, { scale: 1.5, duration: 4, ease: "power1.inOut" });

    const onEnter = () => {
      tl.pause();
      gsap.to(el, { scale: 2.9, duration: 0.8, ease: "power1.out" });
    };
    const onLeave = () => {
      gsap.to(el, {
        scale: 2,
        duration: 3,
        ease: "power1.inOut",
        onComplete: () => tl.resume(),
      });
    };

    el.addEventListener("mouseenter", onEnter);
    el.addEventListener("mouseleave", onLeave);
    return () => {
      el.removeEventListener("mouseenter", onEnter);
      el.removeEventListener("mouseleave", onLeave);
    };
  }, []);

  return (
    <div className="relative h-screen overflow-hidden text-center text-rubik">
      <video autoPlay muted loop playsInline className="absolute w-full h-full object-cover z-0 blur-sm">
        <source src="/videos/WhatsApp Video.mp4" type="video/mp4" />
      </video>

      <Navbar />

      <div className="relative z-10 flex flex-col justify-center items-center h-full text-[#A9CF45] pt-20">
        <div className="relative flex items-center justify-center h-full">
          <img
            src="/images/STOQ Logo Final-png"
            alt="STOQ Logo Text"
            className="w-[1000px] h-[950px] object-contain"
            style={{ transform: "translateX(-70px)" }}
          />

          <div
            className="absolute text-[#6E8A28] text-5xl font-bold text-right font-rubik"
            style={{ bottom: "200px", left: "910px" }}
          >
            SEE.DO.GROW
          </div>

          {/* Color-changing circle inside Q */}
          <div
            className="absolute w-[73px] h-[73px] rounded-full transition-colors duration-1000 z-0"
            style={{
              top: "48%",
              left: "50.2%",
              transform: "translate(-50%, -50%)",
              backgroundColor: colorCycle[colorIndex],
            }}
          ></div>

          {/* TRY Bubble */}
          <div
            ref={tryRef}
            className="try-circle absolute w-[140px] h-[140px] rounded-full overflow-hidden flex items-center justify-center z-20"
            style={{
              top: "49.2%",
              left: "77%",
              transform: "translate(-50%, -50%)",
              cursor: "pointer",
            }}
          >
            <div className="flex flex-col items-center text-center px-2">
              <img
                src="/images/Button Shpehere Black.png"
                alt="TRY"
                className="w-14 h-14 object-contain mb-2"
              />
            </div>
          </div>

          {/* Bubble Animation */}
          <div className="absolute inset-0 flex items-center justify-center pointer-events-none z-0">
            <BubbleAnimation onYChange={(status) => setBubbleDetected(status)} />
          </div>
        </div>

        {/* App Store Buttons */}
        <div className="absolute bottom-10 left-[155px] flex flex-row gap-4 z-20">
          {/* App Store */}
          <a href="#" className="relative w-[200px] h-[60px] inline-block">
            <img src="/images/blur-bg.png" className="absolute w-full h-full" alt="" />
            <div className="absolute inset-0 flex items-center px-3 gap-3 z-10">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                viewBox="0 0 814 1000"
                className="w-10 h-10"
                fill="#44551B"
              >
                <path d="... (your icon path)" />
              </svg>
              <div className="flex flex-col leading-tight">
                <p className="text-[12px] text-[#44551B]">Download on the</p>
                <p className="text-[21px] font-bold text-[#44551B]">App Store</p>
              </div>
            </div>
          </a>

          {/* Google Play */}
          <a href="#" className="relative w-[200px] h-[60px] inline-block">
            <img src="/images/blur-bg.png" className="absolute w-full h-full" alt="" />
            <div className="absolute inset-0 flex items-center px-3 gap-1 z-10">
              <img src="/icons/Google_Play_Arrow_logo.svg" className="w-8 h-8 mr-3" alt="Google Play" />
              <div className="text-left text-sm leading-tight">
                <p className="text-[#44551B] text-[12px]">GET IT ON</p>
                <p className="font-semibold text-[#44551B] text-[21px]">Google Play</p>
              </div>
            </div>
          </a>
        </div>
      </div>
    </div>
  );
};

export default HeroSection;
