import React, { useState, useRef, useEffect } from "react";
import { FaTimes } from "react-icons/fa";

/* ------------------ Reusable Section Component ------------------ */
const VideoSection = ({
  id,
  activeVideo,
  setActiveVideo,
  playInCircle,
  circleRef,
  title,
  subtitle,
  description,
  videoSrc,
  imageSrc,
  reverse = false,
  textColor = "#596F22",
}) => {
  const isActive = activeVideo === id;

  return (
    <section
      className={`flex flex-col ${
        reverse ? "lg:flex-row-reverse" : "lg:flex-row"
      } items-center justify-center px-4 lg:px-10 transition-all duration-500 ease-in-out overflow-hidden relative ${
        isActive ? "h-[596px] py-0" : "py-20"
      }`}
    >
      {/* Video / Circle */}
      <div className="flex-shrink-0 mb-10 lg:mb-0 relative">
        {!isActive ? (
          <div
            ref={circleRef}
            className="w-80 h-80 lg:w-[410px] lg:h-[410px] overflow-hidden rounded-full cursor-pointer shadow-md"
            onClick={() => setActiveVideo(id)}
          >
            {playInCircle ? (
              <video
                src={videoSrc}
                autoPlay
                muted
                loop
                playsInline
                className="object-cover w-full h-full rounded-full"
              />
            ) : (
              <img
                src={imageSrc}
                alt={`Video ${id}`}
                className="object-cover w-full h-full rounded-full"
              />
            )}
          </div>
        ) : (
          <div className="relative w-[90vw] max-w-full h-[500px] rounded-xl overflow-hidden">
            <button
              onClick={() => setActiveVideo(null)}
              className="absolute top-4 right-4 z-10 bg-white text-gray-600 hover:text-black rounded-full p-2 shadow-md"
            >
              <FaTimes className="w-4 h-4" />
            </button>
            <video
              src={videoSrc}
              autoPlay
              loop
              playsInline
              controls
              className="w-full h-full object-contain rounded-xl"
            />
          </div>
        )}
      </div>

      {/* Text */}
      {!isActive && (
        <div
          className={`w-full lg:w-[800px] text-center ${
            reverse ? "lg:mr-16" : "lg:ml-16"
          } lg:text-left`}
        >
          <h2
            className="text-3xl lg:text-5xl font-bold leading-snug mb-4"
            style={{ color: textColor }}
          >
            {title}
          </h2>
          {subtitle && (
            <h3
              className="text-2xl lg:text-5xl font-bold mb-6"
              style={{ color: textColor }}
            >
              {subtitle}
            </h3>
          )}
          <p className="text-[#6E8A28] text-base lg:text-lg leading-relaxed">
            {description}
          </p>
        </div>
      )}
    </section>
  );
};

/* ------------------ Main Component ------------------ */
const SeeDoGrowSection = () => {
  const [activeVideo, setActiveVideo] = useState(null);

  // States for autoplay in circle
  const [playInCircle, setPlayInCircle] = useState({
    1: false,
    2: false,
    3: false,
    4: false,
    5: false,
    6: false,
  });

  // Refs for each circle
  const circleRefs = {
    1: useRef(null),
    2: useRef(null),
    3: useRef(null),
    4: useRef(null),
    5: useRef(null),
    6: useRef(null),
  };

  // Setup intersection observer for each circle
  useEffect(() => {
    Object.keys(circleRefs).forEach((id) => {
      const observer = new IntersectionObserver(
        ([entry]) =>
          setPlayInCircle((prev) => ({ ...prev, [id]: entry.isIntersecting })),
        { threshold: 1 }
      );
      if (circleRefs[id].current) observer.observe(circleRefs[id].current);
      return () => {
        if (circleRefs[id].current) observer.unobserve(circleRefs[id].current);
      };
    });
  }, []);

  return (
    <div className="bg-white font-rubik">
      <div className="bg-white font-rubik">
      {/* Section 1 */}
      <VideoSection
        id={1}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[1]}
        circleRef={circleRefs[1]}
        title="See. Do. Grow."
        subtitle="This is how STOQ works"
        description="See everything in one place. Do what matters in an instant—hand off to a teammate, chat on the item, update its status, restock, or move it where it’s needed. Grow efficiency with zero guesswork and nothing but clear, positive results."
        videoSrc="/videos/LandingPage/main video.mp4"
        imageSrc="/videos/LandingPage/1.png"
        reverse={false}
        textColor="#4F6B1C"
      />

      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

      {/* Section 2 */}
      <VideoSection
        id={2}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[2]}
        circleRef={circleRefs[2]}
        title="Instant clarity from the first use"
        description="STOQ thinks like you do. Its game-style interface makes using STOQ fun—no training required."
        videoSrc="/videos/LandingPage/instant.mp4"
        imageSrc="/videos/LandingPage/2.png"
        reverse={true}
        textColor="#8CB031"
      />

      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

      {/* Section 3 */}
      <VideoSection
        id={3}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[3]}
        circleRef={circleRefs[3]}
        title="One Live Timeline, Zero Chaos."
        description="STOQ lets you run everything with confidence. It maps each item’s life cycle, so you always see what’s on hand, what’s running low, and the next action to take."
        videoSrc="/videos/LandingPage/lifeline 0 chaos.mp4"
        imageSrc="/images/about.jpg"
        reverse={false}
        textColor="#596F22"
      />

      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

      {/* Section 4 */}
      <VideoSection
        id={4}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[4]}
        circleRef={circleRefs[4]}
        title="Your Entire Workflow in One Place"
        description="No juggling apps, no switching tabs. STOQ keeps all your work together, simple and clear."
        videoSrc="/videos/LandingPage/workflow.mp4"
        imageSrc="/videos/LandingPage/4.png"
        reverse={true}
        textColor="#4F6B1C"
      />

      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

      {/* Section 5 */}
      <VideoSection
        id={5}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[5]}
        circleRef={circleRefs[5]}
        title="Every Item, Every Detail, Every Time"
        description="Track every piece of work with precision. Nothing slips through, nothing gets missed."
        videoSrc="/videos/LandingPage/details.mp4"
        imageSrc="/videos/LandingPage/5.png"
        reverse={false}
        textColor="#8CB031"
      />

      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

      {/* Section 6 */}
      <VideoSection
        id={6}
        activeVideo={activeVideo}
        setActiveVideo={setActiveVideo}
        playInCircle={playInCircle[6]}
        circleRef={circleRefs[6]}
        title="Live, always-synced updates"
        description="Everyone sees the same info in real time. No delays, no duplicate effort."
        videoSrc="/videos/LandingPage/Life synd & lasting histpry top.mp4"
        imageSrc="/images/about.jpg"
        reverse={true}
        textColor="#596F22"
      />
    </div>



     {/* lets get started button at center */}
        <div className="flex justify-center py-20">
            <a href="/get-started" className="bg-[#A9CF45] text-black px-6 py-3 rounded-full text-md font-semibold hover:bg-[#8BB833] transition-colors">
            Let's get started
            </a>
        </div>

         {/* Divider Line */}
      <hr className="border-t-2 border-gray-100 mx-20 mb-2" />

    </div>



  );
};

export default SeeDoGrowSection;
