import React from "react";
import { FaStar } from "react-icons/fa";

const testimonials = [
  {
    image: "/images/testimonial1.jpg",
    name: "Alisa Hester",
    role: "PM, Hourglass",
    agency: "Web Design Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial3.jpg",
    quote: `We collectively adore STOQ. It has streamlined our inventory management process. Makes efforts of 5 team members look seamless and consistently impressive.`,
    name: "Rich Wilson",
    role: "COO, Command+R",
    agency: "Web Development Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial2.jpg",
    name: "Annie Stanley",
    role: "Designer, Catalog",
    agency: "UX Agency",
    stars: 4,
  },
  {
    image: "/images/testimonial3.jpg",
    name: "Johnny B",
    role: "PM, Sisyphus",
    agency: "Machine Learning Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial1.jpg",
    name: "Alisa Hester",
    role: "PM, Hourglass",
    agency: "Web Design Agency",
    stars: 5,
  },
];

const TestimonialCard = ({ image, quote, name, role, agency, stars }) => {
  return (
    <div className="relative w-[260px] min-h-[360px] rounded-xl overflow-hidden shadow-md">
      <img src={image} alt={name} className="w-full h-full object-cover" />
      <div className="absolute bottom-4 left-4 right-4 bg-white/30 backdrop-blur-md rounded-lg p-4 text-white">
        {quote ? (
          <>
            <p className="text-sm font-semibold text-white mb-2">"{quote}"</p>
            <div className="flex gap-1 mb-1 text-white">
              {Array(stars)
                .fill(0)
                .map((_, i) => (
                  <FaStar key={i} className="text-white text-xs" />
                ))}
            </div>
            <p className="font-bold text-white">{name}</p>
            <p className="text-sm font-semibold text-white">{role}</p>
            <p className="text-sm text-white">{agency}</p>
          </>
        ) : (
          <>
            <div className="flex gap-1 mb-1">
              {Array(stars)
                .fill(0)
                .map((_, i) => (
                  <FaStar key={i} className="text-white text-xs" />
                ))}
            </div>
            <p className="font-bold text-white">{name}</p>
            <p className="text-sm font-semibold text-white">{role}</p>
            <p className="text-sm text-white">{agency}</p>
          </>
        )}
      </div>
    </div>
  );
};

const TestimonialSection = () => {
  return (
    <section className="py-16 px-6 bg-white text-center">
      <h2 className="text-3xl font-bold text-[#4B6100] mb-10">
        Don’t just take our word for it
      </h2>
      <div className="flex flex-wrap justify-center gap-6">
        {testimonials.map((testimonial, idx) => (
          <TestimonialCard key={idx} {...testimonial} />
        ))}
      </div>

      {/* Navigation Arrows */}
      <div className="mt-8 flex justify-center gap-4">
        <button className="w-10 h-10 flex items-center justify-center rounded-full border text-gray-600 hover:bg-gray-100">
          ←
        </button>
        <button className="w-10 h-10 flex items-center justify-center rounded-full border text-gray-600 hover:bg-gray-100">
          →
        </button>
      </div>
    </section>
  );
};

export default TestimonialSection;
