import React from "react";
import { motion, useScroll, useTransform } from "framer-motion";

const Bubble = ({ text, index }) => {
  const { scrollY } = useScroll();
  const y = useTransform(scrollY, [0, 1000], [0, -200 - index * 50]);

  return (
    <motion.div
      style={{
        y,
        width: 150,
        height: 150,
        borderRadius: "50%",
        background: "white",
        boxShadow: "0 0 15px rgba(0,0,0,0.2)",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "#000",
        fontWeight: "bold",
        fontSize: "14px",
        margin: "60px auto",
        position: "relative",
        zIndex: 2,
      }}
    >
      {text}
    </motion.div>
  );
};

const BubbleScrollSection = () => {
  const items = ["RESTAURANTS", "HOME & CARE", "HOBBYISTS", "KIDS", "FARMERS"];

  return (
    <div
      style={{
        height: "250vh",
        position: "relative",
        overflow: "hidden",
        backgroundImage: `url("/videos/WhatsApp Video.mp4")`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
      }}
    >
      <div style={{ paddingTop: "100px" }}>
        {items.map((item, i) => (
          <Bubble key={i} text={item} index={i} />
        ))}
      </div>
    </div>
  );
};

export default BubbleScrollSection;
