import React from "react";
import { motion, AnimatePresence } from 'framer-motion';

const HeroSection = () => {
        const bubbles = ["Farmers", "Restaurants", "Home & Care", "Hobbyists", "Industry 1", "Industry 2", "Industry 3", "Industry 4"];

        return (
          <div className="relative">
            <video autoPlay muted loop className="absolute top-0 left-0 w-full h-full object-cover z-0">
              <source src="https://www.w3schools.com/html/mov_bbb.mp4" type="video/mp4" />
            </video>
            <div className="flex justify-between items-center p-4 text-white z-10">
              <div className="text-2xl font-bold">STOQ</div>
              <div className="space-x-4">
                <a href="#" className="hover:underline">Industries</a>
                <a href="#" className="hover:underline">Services</a>
                <a href="#" className="hover:underline">Pricing</a>
                <a href="#" className="hover:underline">Resources</a>
                <a href="#" className="hover:underline">About</a>
                <button className="bg-gray-500 text-white px-4 py-2 rounded">Log In</button>
                <button className="bg-green-500 text-white px-4 py-2 rounded">Sign up</button>
              </div>
            </div>
            <div className="flex flex-col items-center justify-center h-screen text-white text-center z-10">
              <h1 className="text-8xl font-bold relative">
                STOQ
                <motion.div 
                  className="bubble-container absolute"
                  animate={{ y: [-200, 0] }}
                  transition={{ duration: 10, repeat: Infinity, ease: "linear" }}
                >
                  {bubbles.map((text, index) => (
                    <motion.div
                      key={index}
                      className="bubble bg-white bg-opacity-50 rounded-full w-32 h-32 flex items-center justify-center text-green-700 font-semibold mx-2"
                      animate={{ y: [0, -20, 0] }}
                      transition={{ duration: 5, repeat: Infinity, ease: "easeInOut" }}
                    >
                      {text}
                    </motion.div>
                  ))}
                </motion.div>
                <motion.button
                  className="try-btn absolute right-0 bottom-0 bg-black text-white rounded-full w-16 h-16 flex items-center justify-center text-sm"
                  whileTap={{ scale: 1.2 }}
                  transition={{ duration: 0.3 }}
                >
                  TRY
                </motion.button>
              </h1>
              <p className="text-2xl mt-4">SEE. DO. GROW</p>
            </div>
          </div>
        );
      };
export default HeroSection;
