import React, { useEffect, useState } from "react";
import Navigation from "./Navbar";

const categories = [
  "FARMERS",
  "RESTAURANTS",
  "HOME & CARE",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
];

const BubbleScrollSection = () => {
  const [yPosition, setYPosition] = useState(0);
  const bubbleSpacing = 180;
  const loopHeight = 1200;

  useEffect(() => {
    let frameId;

    const animate = () => {
      setYPosition((prev) => (prev - 0.5 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };

    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, []);

  const getPosition = (offset) => (yPosition + offset) % loopHeight - loopHeight / 2;

  return (
    <div className="relative h-screen overflow-hidden text-center">
      {/* Video Background */}
      <video
        autoPlay
        muted
        loop
        playsInline
        className="absolute w-full h-full object-cover z-0"
      >
        <source src="/videos/WhatsApp Video.mp4" type="video/mp4" />
      </video>

      {/* Navigation Bar */}
      <div className="absolute top-6 left-0 right-0 z-20 flex justify-center px-4">
        <div className="w-full max-w-screen-xl bg-white/80 backdrop-blur-md border border-gray-200 shadow-md rounded-full px-6 py-3">
          <Navigation />
        </div>
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col justify-center items-center h-full text-[#A9CF45] pt-20">
        <h1 className="flex items-center text-[10rem] font-extrabold tracking-wide">
          S T

          {/* O with vertically looping bubbles */}
          <div className="relative flex justify-center items-center w-[140px] h-[140px] mx-4 overflow-hidden">
            <span className="relative z-20 text-[10rem] text-[#A9CF45] font-extrabold">
              O
            </span>

            {/* Bubble container */}
            <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 z-10">
              {categories.map((text, i) => (
                <div
                  key={i}
                  className="absolute left-1/2 -translate-x-1/2 w-[100px] h-[100px] rounded-full bg-white/80 text-xs text-black font-semibold flex items-center justify-center shadow-md border border-white"
                  style={{
                    transform: `translateY(${getPosition(i * bubbleSpacing)}px)`,
                  }}
                >
                  {text}
                </div>
              ))}
            </div>
          </div>

          {/* Q with TRY */}
          <div className="relative w-[140px] h-[140px] ml-4 text-[10rem] font-extrabold text-[#A9CF45] flex items-center justify-center">
            Q
            <div className="absolute w-[60px] h-[60px] rounded-full bg-black text-white text-base font-bold flex items-center justify-center shadow-inner border-4 border-white">
              TRY
            </div>
          </div>
        </h1>

        {/* Subtitle */}
        <p className="mt-6 text-2xl font-medium text-[#4B6100]">
          SEE. DO. GROW
        </p>
      </div>
    </div>
  );
};

export default BubbleScrollSection;
