import React, { useEffect, useRef } from "react";
import gsap from "gsap";

const Hero = () => {
  const bubbleContainerRef = useRef(null);

  useEffect(() => {
    if (!bubbleContainerRef.current) return;

    const bubbles = bubbleContainerRef.current.querySelectorAll(".bubble");

    gsap.to(bubbles, {
      y: "100vh",
      repeat: -1,
      duration: 6,
      ease: "linear",
      stagger: 0.5,
    });
  }, []);

  const renderBubbles = () => {
    return Array.from({ length: 8 }).map((_, i) => (
      <div
        key={i}
        className="bubble absolute w-5 h-5 rounded-full bg-white opacity-70 left-1/2 -translate-x-1/2"
        style={{ top: `${-80 * i}px` }}
      ></div>
    ));
  };

  return (
    <section className="relative w-full h-screen overflow-hidden text-green-700">
      {/* Background video */}
      <video
        className="absolute top-0 left-0 w-full h-full object-cover z-0"
        src="/assets/background.mp4"
        autoPlay
        muted
        loop
      ></video>

      {/* Bubble container (inside O) */}
      <div
        ref={bubbleContainerRef}
        className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 w-24 h-48 z-20 overflow-hidden pointer-events-none"
      >
        {renderBubbles()}
      </div>

      {/* STOQ + Circles */}
      <div className="z-10 relative h-full flex flex-col items-center justify-center">
        <div className="flex items-center gap-4 text-[100px] font-extrabold">
          <span>S</span>
          <span>T</span>
          <span>O</span>
          <span>Q</span>
        </div>

        {/* Circle buttons */}
        <div className="flex gap-8 mt-10">
          {["SERVICES", "CONSTRUCTION", "WORKSHOPS"].map((text, i) => (
            <div
              key={i}
              className="w-32 h-32 rounded-full bg-gradient-to-br from-green-300 to-white flex items-center justify-center font-bold text-center shadow-md"
            >
              {text}
            </div>
          ))}
        </div>

        {/* Subtext */}
        <p className="mt-10 text-xl font-bold">SEE. DO. GROW</p>
      </div>
    </section>
  );
};

export default Hero;
