import React from "react";
import { FaCheckCircle } from "react-icons/fa";

const plans = [
  {
    title: "Standard Pro",
    price: "Free",
    subtitle: "Standard Pro features for 1 user.",
    features: [
      "1 user",
      "10GB of storage",
      "Basic inventory tracking",
    ],
    highlight: false,
  },
  {
    title: "Artisan",
    price: "$9.99",
    per: "per month",
    subtitle: "Advanced features and reporting.",
    features: [
      "1 admin user",
      "5 users for team collaboration",
      "30GB of storage",
      "Bonus: 1 family cluster, adding 10GB of storage",
      "Additional admin users for $4.99 per admin/month",
    ],
    highlight: true,
    badge: "Popular",
  },
  {
    title: "Business",
    price: "$19.99",
    per: "per month",
    subtitle: "Unlimited features.",
    features: [
      "Unlimited admin users ($9.99 per admin/month)",
      "Unlimited team collaborators ($0.50 per user/month)",
      "50GB of storage",
      "Bonus: up to 5 STOQ clusters, adding an extra 500GB of storage",
    ],
    highlight: false,
  },
];

const PricingPlans = () => {
  return (
    <section className="bg-white py-20 px-4 text-center">
      <h2 className="text-2xl md:text-3xl font-semibold text-[#4B6100]">
        Choose your plan
      </h2>
      <h3 className="text-3xl md:text-4xl font-bold text-[#A9CF45] mt-2 mb-10">
        Professional
      </h3>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-6xl mx-auto">
        {plans.map((plan, index) => (
          <div
            key={index}
            className={`rounded-xl border border-gray-200 shadow-sm p-6 flex flex-col justify-between ${
              plan.highlight ? "bg-[#F8FAF5]" : "bg-[]"
            }`}
          >
            {/* Badge */}
            {plan.badge && (
              <span className="bg-[#E4F2C6] text-[#4B6100] text-xs font-semibold px-2 py-1 rounded-full self-end mb-2">
                {plan.badge}
              </span>
            )}

            {/* Title */}
            <h4 className="text-left text-lg font-semibold text-gray-700">
              {plan.title}
            </h4>

            {/* Price */}
            <div className="text-left mt-2 mb-1">
              <p className="text-3xl md:text-4xl font-extrabold text-black">
                {plan.price}
                {plan.per && (
                  <span className="text-sm font-medium text-gray-600 ml-1">
                    {plan.per}
                  </span>
                )}
              </p>
              <p className="text-sm text-gray-500 mt-1">{plan.subtitle}</p>
            </div>

            {/* CTA */}
            <button className="mt-4 bg-[#A9CF45] hover:bg-[#8BB833] text-white font-semibold py-2 px-4 rounded-full">
              Get started
            </button>

            {/* Features */}
            <div className="mt-6 text-left">
              <h5 className="text-sm font-bold text-gray-700 mb-2">FEATURES</h5>
              <ul className="space-y-2 text-sm text-gray-600">
                {plan.features.map((feature, i) => (
                  <li key={i} className="flex items-start gap-2">
                    <FaCheckCircle className="text-[#A9CF45] mt-0.5" />
                    <span>{feature}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default PricingPlans;
