import React from "react";
import { motion } from "framer-motion";

const categories = [
  "KIDS",
  "FREELANCERS",
  "WELLNESS",
  "SERVICES",
  "RESTAURANTS",
  "HOME & CARE",
];

const HeroSection = () => {
  return (
    <div className="relative h-screen overflow-hidden text-center">
      {/* Background Video */}
      <video
        autoPlay
        muted
        loop
        playsInline
        className="absolute w-full h-full object-cover z-0"
      >
        <source src="/videos/WhatsApp Video.mp4" type="video/mp4" />
      </video>

      {/* Overlay Content */}
      <div className="relative z-10 flex flex-col justify-center items-center h-full text-[#8BC34A]">
        {/* STOQ */}
        <h1 className="flex items-center text-[10rem] font-extrabold tracking-wide">
          S T
          {/* O with Bubbles */}
          <div className="relative flex justify-center items-center w-[140px] h-[140px] mx-2">
            {/* Animated Bubbles Inside O */}
            <div className="absolute top-0 left-1/2 -translate-x-1/2 w-[140px] h-[1000px] overflow-hidden pointer-events-none z-10">
              {categories.map((text, i) => (
                <motion.div
                  key={i}
                  className="absolute left-1/2 -translate-x-1/2 w-[120px] h-[120px] rounded-full bg-white/80 text-sm text-black font-semibold flex items-center justify-center shadow-lg backdrop-blur-sm border border-white"
                  animate={{
                    y: ["100vh", "-200%"],
                    opacity: [1, 1, 0],
                  }}
                  transition={{
                    duration: 8,
                    repeat: Infinity,
                    delay: i * 1.5,
                  }}
                >
                  {text}
                </motion.div>
              ))}
            </div>

            <span className="relative z-20 text-[10rem] text-[#8BC34A] font-extrabold">O</span>
          </div>

          {/* Q with TRY */}
          <div className="relative w-[140px] h-[140px] text-[10rem] font-extrabold text-[#8BC34A] flex items-center justify-center">
            Q
            <div className="absolute w-[60px] h-[60px] rounded-full bg-black text-white text-base font-bold flex items-center justify-center shadow-inner border-4 border-white">
              TRY
            </div>
          </div>
        </h1>

        {/* Subtitle */}
        <p className="mt-4 text-2xl font-medium text-[#4B6100]">SEE. DO. GROW</p>
      </div>
    </div>
  );
};

export default HeroSection;
