// src/components/BubbleAnimation.jsx
import React, { useEffect, useState } from "react";

const categories = [
  "FARMERS",
  "RESTAURANTS", 
  "HOME & CARE",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
  "CONSTRUCTION",
  "WORKSHOPS",
  "KIDS",
];

const BubbleAnimation = () => {
  const [yPosition, setYPosition] = useState(0);
  const spacing = 270;
  const loopHeight = spacing * categories.length;

  useEffect(() => {
    let frameId;
    const animate = () => {
      setYPosition((prev) => (prev - 2 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };
    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, []);

  const getY = (offset) => {
    const rawY = (yPosition + offset) % loopHeight;
    return rawY > loopHeight / 2 ? rawY - loopHeight : rawY;
  };

  return (
    // Wrapper to adjust horizontal position
    <div className="absolute top-0 left-0 w-full h-full pointer-events-none z-10">
      <div
        className="relative"
        style={{
          position: "absolute",
          top: "50%",
          left: "41%", // adjust this to move bubbles left/right
          transform: "translate(-50%, -50%)",
        }}
      >
        {categories.map((text, i) => {
          const y = getY(i * spacing);
          return (
            <div
              key={i}
              className="absolute w-[280px] h-[230px] flex flex-col items-center justify-center text-center"
              style={{
                transform: `translateY(${y}px)`,
                opacity: Math.abs(y) > loopHeight / 2 - 140 ? 0 : 1, // fade at edges
              }}
            >
              {/* SOLUTION 2: Pure CSS Frosted Glass Bubble */}
              <div 
                className="relative w-[230px] h-[220px] rounded-full flex items-center justify-center"
                style={{
                  background: "rgba(255, 255, 255, 0.1)",
                  backdropFilter: "blur(12px)",
                  WebkitBackdropFilter: "blur(12px)",
                  border: "1px solid rgba(255, 255, 255, 0.2)",
                  boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.2)"
                }}
              >
                {/* Optional: Add some gradient overlay for more depth */}
                <div 
                  className="absolute inset-0 rounded-full"
                  style={{
                    background: "linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)"
                  }}
                />
              </div>

              {/* Optional: Add category label inside or below */}
              <span className="absolute bottom-19 text-[18px] font-semibold text-black">
                {text}
              </span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default BubbleAnimation;
