import React from "react";
import { motion } from "framer-motion";


const categories = [
    "FARMERS",
    "RESTAURANTS",
    "HOME & CARE",
    "HOBBYISTS",
    "SERVICES",
    "WELLNESS",
  ];
const BubbleScrollSection = () => {
    return (
        <div className="relative h-screen overflow-hidden text-center">
          {/* Background Video */}
          <video
            autoPlay
            muted
            loop
            playsInline
            className="absolute w-full h-full object-cover z-0"
          >
            <source src="/videos/WhatsApp Video.mp4" type="video/mp4" />
          </video>

          {/* Bubbles Floating from Bottom to Top */}
          <div className="fixed left-1/2 top-0 -translate-x-1/2 w-[140px] h-screen z-10 pointer-events-none">
            {categories.map((text, i) => (
              <motion.div
                key={i}
                className="absolute bottom-[-150px] left-1/2 -translate-x-1/2 w-[120px] h-[120px] rounded-full bg-white/80 text-black text-base font-bold flex items-center justify-center shadow-lg backdrop-blur-md border border-white"
                animate={{
                  y: ["100vh", "-150px"],
                  opacity: [1, 1, 0],
                }}
                transition={{
                  duration: 12,
                  repeat: Infinity,
                  delay: i * 1.5,
                  ease: "linear",
                }}
              >
                {text}
              </motion.div>
            ))}
          </div>

          {/* Main Content */}
          <div className="relative z-20 flex flex-col justify-center items-center h-full text-[#8BC34A]">
            <h1 className="flex items-center text-[10rem] font-extrabold tracking-wide">
              S T

              {/* O in the middle */}
              <span className="relative z-30 mx-2 text-[10rem] text-[#8BC34A] font-extrabold">O</span>

              {/* Q with TRY */}
              <div className="relative w-[140px] h-[140px] text-[10rem] font-extrabold text-[#8BC34A] flex items-center justify-center">
                Q
                <div className="absolute w-[60px] h-[60px] rounded-full bg-black text-white text-base font-bold flex items-center justify-center shadow-inner border-4 border-white">
                  TRY
                </div>
              </div>
            </h1>

            <p className="mt-4 text-2xl font-medium text-[#4B6100]">SEE. DO. GROW</p>
          </div>
        </div>
      );
    };
export default BubbleScrollSection;
