// src/components/BubbleAnimation.jsx
import React, { useEffect, useState } from "react";

const categories = [
  "FARMERS",
  "RESTAURANTS",
  "HOME & CARE",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
  "CONSTRUCTION",
  "WORKSHOPS",
  "KIDS",
  "SERVICES",

];

const BubbleAnimation = () => {
  const [yPosition, setYPosition] = useState(0);
  const spacing = 150;
  const loopHeight = 1200;

  useEffect(() => {
    let frameId;
    const animate = () => {
      setYPosition((prev) => (prev - 0.5 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };
    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, []);

  const getY = (offset) => (yPosition + offset) % loopHeight - loopHeight / 2;

  return (
    <div className="absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 z-10 pointer-events-none">
      {categories.map((text, i) => (
        <div
          key={i}
          className="absolute left-1/2 -translate-x-1/2 w-[140px] h-[140px] rounded-full bg-white/80 text-xs text-black font-semibold flex items-center justify-center shadow-md border border-white"
          style={{
            transform: `translateY(${getY(i * spacing)}px)`,
            background: "rgba(255, 255, 255, 0.3)", // translucent white
            backdropFilter: "blur(12px)", // blur the background
            WebkitBackdropFilter: "blur(12px)", // Safari support
            border: "1px solid rgba(255, 255, 255, 0.6)", // subtle border
          }}
        >
          {text}
        </div>
      ))}
    </div>
  );
};

export default BubbleAnimation;
