// components/VideoCircle.jsx
import React, { useState } from "react";

const VideoCircle = ({ imageSrc, videoSrc }) => {
  const [isPlaying, setIsPlaying] = useState(false);

  return (
    <div className="relative w-80 h-80 lg:w-[410px] lg:h-[410px] rounded-full overflow-hidden">
      {isPlaying ? (
        <>
          <div className="absolute inset-0 w-full h-[592px] z-10 bg-black rounded-none lg:rounded-xl">
            <video
              src={videoSrc}
              autoPlay
              controls
              className="w-full h-full object-cover"
            />
            <button
              onClick={() => setIsPlaying(false)}
              className="absolute top-4 right-4 text-white bg-black/70 w-8 h-8 rounded-full flex items-center justify-center"
            >
              âœ•
            </button>
          </div>
        </>
      ) : (
        <img
          src={imageSrc}
          alt="thumbnail"
          className="w-full h-full object-cover cursor-pointer"
          onClick={() => setIsPlaying(true)}
        />
      )}
    </div>
  );
};

export default VideoCircle;
