import Navbar from "../../components/Navbar";
import React, { useEffect, useRef } from "react";
import gsap from "gsap";

import Footer from "../../components/Footer";
import TestimonialSection from "../../components/TestimonialSection";
import ProffessionalPricingPlans from "../../components/ProffessionalPricingPlans";

const ConstructionPage = () => {
  const scrollRef = useRef(null);
  const tryRef = useRef(null);

  // Auto scroll for feature list
  useEffect(() => {
    const container = scrollRef.current;
    let scrollAmount = 0;
    let speed = 0.5;

    function autoScroll() {
      if (container) {
        scrollAmount += speed;
        container.scrollLeft = scrollAmount;
        if (scrollAmount >= container.scrollWidth / 2) {
          scrollAmount = 0;
        }
      }
      requestAnimationFrame(autoScroll);
    }
    autoScroll();
  }, []);

  // GSAP bubble animation
  useEffect(() => {
    const el = tryRef.current;
    gsap.set(el, { scale: 1.5 });
    const tl = gsap.timeline({ repeat: -1, yoyo: true });
    tl.to(el, { scale: 2.2, duration: 4, ease: "power1.inOut" })
      .to(el, { scale: 1.3, duration: 4, ease: "power1.inOut" });

    const onEnter = () => {
      tl.pause();
      gsap.to(el, { scale: 2.2, duration: 0.8, ease: "power1.out" });
    };
    const onLeave = () => {
      gsap.to(el, {
        scale: 1.5,
        duration: 3,
        ease: "power1.inOut",
        onComplete: () => tl.resume(),
      });
    };

    el.addEventListener("mouseenter", onEnter);
    el.addEventListener("mouseleave", onLeave);
    return () => {
      el.removeEventListener("mouseenter", onEnter);
      el.removeEventListener("mouseleave", onLeave);
    };
  }, []);

  const items = [
    { img: "qr.png", label: "QR tagging" },
    { img: "site.png", label: "Site/location mapping" },
    { img: "inventory.png", label: "Centralized inventory" },
    { img: "role.png", label: "Role-based access" },
    { img: "ui.png", label: "Intuitive UI" },
    { img: "account.png", label: "Team accountability" },
    { img: "return.png", label: "Return/ transfer items" },
    { img: "dash.png", label: "Usage analytics dashboard" },
    { img: "business.png", label: "Efficient business" },
  ];

  return (
    <div className="overflow-x-hidden">
      {/* Hero Section */}
      <section className="relative h-screen overflow-hidden font-rubik">
        <video
          autoPlay
          muted
          loop
          playsInline
          className="absolute w-full h-full object-cover z-0 blur-sm"
        >
          <source src="/videos/WhatsApp Video.mp4" type="video/mp4" />
        </video>

        <Navbar />

        <div className="relative z-10 flex flex-col justify-center items-center h-full text-[#A9CF45]">
          <div className="relative w-full max-w-[1600px] flex flex-col items-center justify-center px-6 md:px-12">

            {/* Top Text */}
            <div className="absolute top-[10%] left-[5%] max-w-lg">
              <h2 className="text-[#6E8A28] text-3xl md:text-5xl font-bold">
                CONSTRUCTION <span>Inventory Management.</span>
              </h2>
              <p className="text-[#63645c] text-base md:text-xl mt-2 font-semibold">
                See your material resources. Do what's necessary. Grow with no stress and full control.
              </p>
            </div>

            {/* Logo Centered */}
            <div className="relative flex justify-center items-center w-full">
              <img
                src="/images/STOQ Logo Final.png"
                alt="STOQ Logo"
                className="max-w-[70vw] max-h-[70vh] object-contain"
              />

              {/* Center Bubble */}
              <div className="absolute flex items-center justify-center rounded-full text-[2vw] font-bold text-black bg-white/20 border border-white/40 backdrop-blur-xl shadow-lg w-[15vw] h-[15vw] max-w-[240px] max-h-[240px]">
                CONSTRUCTION
              </div>

              {/* TRY Button */}
              <div
                ref={tryRef}
                className="absolute cursor-pointer flex items-center justify-center rounded-full overflow-hidden w-[18vw] h-[18vw] max-w-[280px] max-h-[280px]"
                style={{ transform: "translate(120%, 0)" }}
              >
                <img
                  src="/images/Button Shpehere Black.png"
                  alt="TRY"
                  className="w-[50%] h-[50%] object-contain"
                />
              </div>
            </div>

            {/* Slogan */}
            <p className="absolute bottom-[15%] right-[8%] text-[#6E8A28] text-2xl md:text-4xl font-bold">
              SEE. DO. GROW.
            </p>
          </div>
        </div>
      </section>

      {/* Feature Section */}
      <section className="bg-white flex flex-col items-center px-6 md:px-28 py-20 font-rubik">
        <div className="w-full max-w-screen-2xl">
          <h1 className="text-3xl md:text-5xl font-bold text-[#181D27] mb-4">
            Home runs better with <span>STOQ</span>.
          </h1>
          <p className="text-gray-600 text-lg mb-16 max-w-2xl">
            See what's on hand, act together, and evolve into everyday peace of mind.
          </p>

          {/* Auto Scroll Features */}
          <div ref={scrollRef} className="overflow-x-hidden scrollbar-hide whitespace-nowrap">
            <div className="flex gap-8 w-max">
              {[...items, ...items].map((item, idx) => (
                <div key={idx} className="flex flex-col items-center flex-shrink-0 w-[280px] md:w-[320px]">
                  <img
                    src={`/images/construction/${item.img}`}
                    alt={item.label}
                    className="w-[250px] h-[250px] md:w-[350px] md:h-[350px] object-contain mb-2"
                  />
                  <p className="text-lg md:text-xl font-semibold text-center text-gray-800">
                    {item.label}
                  </p>
                </div>
              ))}
            </div>
          </div>

          {/* Start Button */}
          <div className="flex justify-center items-center pt-20">
            <a
              href="/get-started"
              className="inline-block w-[200px] h-[80px] bg-no-repeat bg-center bg-contain text-center text-black text-lg font-semibold leading-[80px]"
              style={{ backgroundImage: "url('/images/Actions.png')" }}
            >
              Let's get started
            </a>
          </div>
        </div>
      </section>

      {/* Info Section */}
      <section className="relative w-full bg-white pt-16 pb-24 overflow-hidden font-rubik">
        <div className="relative z-10 w-full grid grid-cols-1 md:grid-cols-2 gap-16 px-6 md:px-24">
          <div className="flex flex-col text-lg">
            <h2 className="text-[#6E8A28] font-medium text-3xl md:text-5xl mb-6">
              STOQ in Action.
            </h2>
            <p className="text-[#535862] mb-4">
              For Small Construction Businesses.
            </p>
            <p className="text-[#535862] mb-4">
              STOQ is the small Construction business inventory management app that serves as your real-time operations layer for physical resources.
            </p>
            <p className="text-[#535862] mb-4">
              STOQ fits any role. Use it solo or with teams and partners.
            </p>
