import clsx from "clsx";

const sizeClasses = {
  sm: "px-4 py-2 text-sm w-[120px] h-[36px]",
  md: "px-6 py-3 text-base w-[160px] h-[44px]",
  lg: "px-8 py-3.5 text-lg w-[200px] h-[52px]",
  xl: "px-[18px] py-[12px] text-lg w-[528px] h-[48px]", // from Figma
};

export default function Button({
  children,
  size = "md",
  onClick,
  className,
}) {
  return (
    <button
      className="
        w-full
        bg-[#A9CF45] hover:bg-[#8BB833]
        text-black font-semibold
        py-3 px-6
        rounded-full
        mb-6
        border-2 border-[#96b34d]
        shadow-sm
        flex items-center justify-center   /* ✅ center text */
        text-lg leading-none              /* ✅ prevents vertical shift */
      "
    >
      {children}
    </button>
  );
}
