import React from "react";
import { FaCheck } from "react-icons/fa";

const plans = [
  {
    title: "Standard Pro",
    price: "Free",
    subtitle: "Standard Pro features for 1 user.",
    features: [
      "1 user",
      "1GB of storage",
      "Basic inventory tracking",
    ],
    prefix: "Standard",
  },
  {
    title: "Artisan",
    price: "$9,99",
    per: "per month",
    subtitle: "Advanced features and reporting.",
    features: [
      "1 admin user",
      "5 users for team collaboration",
      "30GB of storage",
      "Bonus: 1 family cluster, adding 10GB of storage",
      "Additional admin users for $4.99 per admin/month",
    ],
    prefix: "Starter",
    badge: "Popular",
  },
  {
    title: "Business",
    price: "$19,99",
    per: "per month",
    subtitle: "Unlimited features.",
    features: [
      "Unlimited admin users )$9,99 per admin/month)",
      "Unlimited team collaborators ($0,50 per user/month)",
      "50GB of storage",
      "Bonus: up to 5 STOQ clusters, adding an extra 50GB of storage",
    ],
    prefix: "Artisan Pro",
  },
];

const PricingPlans = () => {
  return (
    <section className="bg-white py-20 px-4 text-center">
      <h2 className="text-2xl md:text-3xl font-semibold text-[#4B6100]">
        Choose your plan
      </h2>
      <h3 className="text-3xl md:text-4xl font-bold text-[#A9CF45] mt-2 mb-10">
        Professional
      </h3>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl mx-auto">
        {plans.map((plan, index) => (
          <div
            key={index}
            className="bg-[#FBFDF6] rounded-xl border border-gray-200 shadow-sm p-6 flex flex-col justify-between relative"
          >
            {/* Badge */}
            {plan.badge && (
              <span className="absolute top-6 right-6 bg-[#E4F2C6] text-[#4B6100] text-[11px] font-semibold px-3 py-1 rounded-full uppercase">
                {plan.badge}
              </span>
            )}

            {/* Title & Price */}
            <div className="text-left mb-8">
              <h4 className="text-lg font-semibold text-gray-800 mb-2">
                {plan.title}
              </h4>
              <p className="text-[40px] font-extrabold text-black leading-none">
                {plan.price}
                {plan.per && (
                  <span className="text-sm font-medium text-gray-600 ml-2">
                    {plan.per}
                  </span>
                )}
              </p>
              <p className="text-sm text-gray-600 mt-2">{plan.subtitle}</p>
            </div>

            {/* Button */}
            <div className="mt-auto mb-6">
              <button className="w-full bg-[#A9CF45] hover:bg-[#8BB833] text-black font-semibold py-2.5 rounded-full text-sm">
                Get started
              </button>
            </div>

            {/* Divider */}
            <div className="border-t border-gray-200 mb-6"></div>

            {/* Features */}
            <div className="text-left">
              <h5 className="text-sm font-bold text-gray-800 mb-3">FEATURES</h5>
              <p className="text-sm text-gray-700 font-medium mb-4">
                Everything in{" "}
                <span className="font-bold">{plan.prefix}</span> plus….
              </p>
              <ul className="space-y-3">
                {plan.features.map((feature, i) => (
                  <li key={i} className="flex items-start gap-3 text-sm text-gray-700">
                    <span className="w-5 h-5 rounded-full border border-[#A9CF45] flex items-center justify-center mt-1">
                      <FaCheck className="text-[#A9CF45] text-xs" />
                    </span>
                    <p>{feature}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default PricingPlans;
