import React from "react";
import { FaCheck } from "react-icons/fa";

const plans = [
  {
    title: "Standard Pro",
    price: "Free",
    subtitle: "Standard Pro features for 1 user.",
    features: [
      "1 user",
      "1GB of storage",
      "Basic inventory tracking",
    ],
    prefix: "Standard",
  },
  {
    title: "Artisan",
    price: "$9,99",
    per: "per month",
    subtitle: "Advanced features and reporting.",
    features: [
      "1 admin user",
      "5 users for team collaboration",
      "30GB of storage",
      "Bonus: 1 family cluster, adding 10GB of storage",
      "Additional admin users for $4.99 per admin/month",
    ],
    prefix: "Starter",
    badge: "Popular",
  },
  {
    title: "Business",
    price: "$19,99",
    per: "per month",
    subtitle: "Unlimited features.",
    features: [
      "Unlimited admin users )$9,99 per admin/month)",
      "Unlimited team collaborators ($0,50 per user/month)",
      "50GB of storage",
      "Bonus: up to 5 STOQ clusters, adding an extra 50GB of storage",
    ],
    prefix: "Artisan Pro",
  },
];

const PricingPlans = () => {
  return (
    <section className="bg-white py-20 px-4 text-center">
      <h2 className="text-2xl md:text-3xl font-semibold text-[#4B6100]">
        Choose your plan
      </h2>
      <h3 className="text-3xl md:text-4xl font-bold text-[#A9CF45] mt-2 mb-10">
        Professional
      </h3>


    </section>
  );
};

export default PricingPlans;
