import React, { useRef } from "react";
import { FaStar } from "react-icons/fa";
import { FaArrowLeft, FaArrowRight } from "react-icons/fa";

const testimonials = [
  {
    image: "/images/testimonial1.jpg",
    name: "Alisa Hester",
    role: "PM, Hourglass",
    agency: "Web Design Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial3.jpg",
    quote: `We collectively adore STOQ. It has streamlined our inventory management process. Makes efforts of 5 team members look seamless and consistently impressive.`,
    name: "Rich Wilson",
    role: "COO, Command+R",
    agency: "Web Development Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial2.jpg",
    name: "Annie Stanley",
    role: "Designer, Catalog",
    agency: "UX Agency",
    stars: 4,
  },
  {
    image: "/images/testimonial3.jpg",
    name: "Johnny B",
    role: "PM, Sisyphus",
    agency: "Machine Learning Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial1.jpg",
    name: "Alisa Hester",
    role: "PM, Hourglass",
    agency: "Web Design Agency",
    stars: 5,
  },
  {
    image: "/images/testimonial1.jpg",
    name: "Alisa Hester",
    role: "PM, Hourglass",
    agency: "Web Design Agency",
    stars: 5,
  },
];


const TestimonialSection = () => {
    const sliderRef = useRef();

    const scroll = (direction) => {
      if (!sliderRef.current) return;
      const { scrollLeft, offsetWidth } = sliderRef.current;
      sliderRef.current.scrollTo({
        left: direction === "left" ? scrollLeft - offsetWidth : scrollLeft + offsetWidth,
        behavior: "smooth",
      });
    };

    return (
      <section className="py-16 pl- bg-white">
        <h2 className="text-3xl font-bold text-[#4B6100] mb-8">
          Donâ€™t just take our word for it
        </h2>

        <div className="relative">
          {/* Left Arrow */}
          <button
            onClick={() => scroll("left")}
            className="absolute left-0 top-1/2 transform -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full w-10 h-10 flex items-center justify-center shadow-md hover:shadow-lg"
          >
            <FaArrowLeft />
          </button>

          {/* Right Arrow */}
          <button
            onClick={() => scroll("right")}
            className="absolute right-0 top-1/2 transform -translate-y-1/2 z-10 bg-white border border-gray-300 rounded-full w-10 h-10 flex items-center justify-center shadow-md hover:shadow-lg"
          >
            <FaArrowRight />
          </button>

          {/* Slider */}
          <div
            ref={sliderRef}
            className="flex gap-6 overflow-x-auto scroll-smooth scrollbar-hide snap-x snap-mandatory"
          >
            {testimonials.map((t, i) => (
              <div
                key={i}
                className="min-w-[300px] md:min-w-[320px] snap-start bg-white rounded-xl overflow-hidden shadow-md relative"
              >
                <img
                  src={t.image}
                  alt={t.name}
                  className="w-full h-80 object-cover"
                />
                <div className="absolute bottom-4 left-4 right-4 bg-white/70 backdrop-blur-md p-4 rounded-lg">
                  {t.quote ? (
                    <p className="text-sm text-black font-semibold mb-2">"{t.quote}"</p>
                  ) : (
                    <>
                      <div className="flex text-yellow-500 mb-1">
                        {Array(t.rating).fill().map((_, i) => (
                          <FaStar key={i} />
                        ))}
                      </div>
                      <p className="font-bold text-black">{t.name}</p>
                      <p className="text-gray-600 text-sm">{t.title}</p>
                      <p className="text-gray-500 text-xs">{t.agency}</p>
                    </>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  };

export default TestimonialSection;
