import React from "react";
import Navbar from "../components/Navbar";


export default function AboutPage() {
    return (
        <section className="relative w-full h-[376px] overflow-hidden">
        {/* Background Video */}
        <div className="absolute inset-0 z-0">
          <video
            autoPlay
            muted
            loop
            playsInline
            className="w-full h-full object-contain"
          >
            <source src="/videos/background-new.mp4" type="video/mp4" />
          </video>

          {/* Overlay layer */}
          <div className="absolute inset-0 bg-white/40 backdrop-blur-[10px]" />
        </div>

         {/* Navbar - Fixed */}
         <div className="fixed top-0 left-0 w-full z-50 bg-white shadow-md">
          <Navbar />
        </div>


        {/* Content */}
        <div className="relative max-w-[1440px] h-full mx-auto px-8 flex flex-col justify-center">
          <div className="flex flex-col md:flex-row items-start md:items-center justify-between h-full">

            {/* Left text block */}
            <div className="max-w-[50%] space-y-2">
              <p className="text-sm font-medium text-gray-800">Nice to meet you</p>
              <h1 className="text-4xl font-extrabold text-[#1C2A13]">About our company</h1>
            </div>

            {/* Right text block */}
            <div className="max-w-[40%] mt-4 md:mt-0">
              <p className="text-[#3C4C2F] text-lg leading-relaxed">
                See how STOQ adapts to real-life workflows, <br />
                across industries and lifestyles.
              </p>
            </div>


          </div>
        </div>
      </section>
    );
  }
