import React, { useEffect, useState } from "react";

const categories = [
  "FARMERS",
  "HOUSEHOLDS",
  "FOOD & COOK",
  "HOBBYISTS",
  "FREELANCERS",
  "WELLNESS",
  "CONSTRUCTION",
  "WORKSHOPS",
  "KIDS",
];

const BubbleAnimation = ({
  onPositionChange,
  selectedCategory,
  setSelectedCategory,
  animationStopped,
  setAnimationStopped,
}) => {
  const [yPosition, setYPosition] = useState(0);

  // spacing based on relative % of container height
  const spacing = 35; // % instead of fixed px
  const loopHeight = spacing * categories.length;

  useEffect(() => {
    if (animationStopped) return;
    let frameId;
    const animate = () => {
      setYPosition((prev) => (prev - 1 + loopHeight) % loopHeight);
      frameId = requestAnimationFrame(animate);
    };
    frameId = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frameId);
  }, [loopHeight, animationStopped]);

  useEffect(() => {
    if (!onPositionChange) return;
    const container = document.querySelector("#bubble-container");
    if (!container) return;

    const bubbles = Array.from(container.querySelectorAll(".bubble-item")).map((el) => {
      const rect = el.getBoundingClientRect();
      return {
        x: rect.left + rect.width / 2,
        y: rect.top + rect.height / 2,
        radius: rect.width / 2,
      };
    });
    onPositionChange(bubbles);
  }, [yPosition, onPositionChange]);

  const getY = (offset) => {
    const rawY = (yPosition + offset) % loopHeight;
    return rawY > loopHeight / 2 ? rawY - loopHeight : rawY;
  };

  return (
    <div className="absolute inset-0 pointer-events-auto z-10 font-rubik">
      <div
        id="bubble-container"
        className="relative"
        style={{
          position: "absolute",
          top: "50%",
          left: "50%", // keep centered on logo
          transform: "translate(-50%, -50%)",
        }}
      >
        {categories.map((text, i) => {
          const y = getY(i * spacing);
          const isSelected = selectedCategory === text;

          // Fix bubble style when selected
          const fixedStyle =
            isSelected && animationStopped
              ? {
                  position: "fixed",
                  top: "10%", // adjust relative to logo height
                  left: "50%",
                  transform: "translate(-50%, -50%)",
                  zIndex: 30,
                }
              : {
                  transform: `translateY(${y}%)`,
                };

          return (
            <div
              key={i}
              className="bubble-item absolute rounded-full font-bold text-black flex items-center justify-center cursor-pointer"
              style={{
                ...fixedStyle,
                width: "22%", // scales with logo
                aspectRatio: "1/1",
                fontSize: "1.2vw", // scales with viewport
                background: `linear-gradient(180deg, rgba(255, 255, 255, 0.3) 0%, rgba(255, 255, 255, 0.1) 100%)`,
                border: "1px solid rgba(255, 255, 255, 0.4)",
                boxShadow: `
                  inset 0 0 10px rgba(255, 255, 255, 0.3),
                  inset 0 2px 4px rgba(255, 255, 255, 0.2),
                  0 4px 8px rgba(0, 0, 0, 0.05)
                `,
                backdropFilter: "blur(20px)",
                WebkitBackdropFilter: "blur(20px)",
                opacity: Math.abs(y) > loopHeight / 2 - 20 && !isSelected ? 0 : 1,
              }}
              onClick={() => {
                if (isSelected && animationStopped) {
                  setSelectedCategory(null);
                  setAnimationStopped(false);
                } else if (!animationStopped) {
                  setSelectedCategory(text);
                  setAnimationStopped(true);
                }
              }}
            >
              {text}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default BubbleAnimation;
